/*==================================================
src/components/AccountBalance.js

The AccountBalance component displays account balance. It is included in other page views.
==================================================*/
import React, { Component } from 'react';

class AccountBalance extends Component {
  render() {
    const { credits, debits } = this.props;

    // Calculate total credits
    const totalCredits = credits.reduce((total, credit) => total + credit.amount, 0);

    // Calculate total debits
    const totalDebits = debits.reduce((total, debit) => total + debit.amount, 0);

    // Calculate account balance
    const accountBalance = totalCredits - totalDebits;

    return (
      <div>
        {/* Display account balance with 2 decimal places */}
        Balance: {accountBalance.toFixed(2)}
      </div>
    );
  }
}

export default AccountBalance;
